package typecasting;

public class Typecasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//This program will check the narrowing and widening as this called the manual and automatic typecasting techniques.
		System.out.println("Automatic typecasting also known as widening");
		double x=5.54734687;
	float y =(float) x;
		System.out.println(x);
		System.out.println(y);
		float z=5f;
		System.out.println(z);

	}

}
